mapper.insert:
insert into
    tbl_menu
    (
      menu_name
    , menu_price
    , category_code
    , orderable_status
    )
values
    (
      #{menuName}
    , #{menuPrice}
    , #{categoryCode}
    , #{orderableStatus}
    )

mapper.update:
update
    tbl_menu
set
    menu_name = #{menuName}
  , menu_price = #{menuPrice}
  , category_code = #{categoryCode}
  , orderable_status = #{orderableStatus}
where
    menu_code = #{menuCode}
